INSERT INTO `fly_news` (`id`, `type_id`, `sort`, `title`, `keywords`, `description`, `defaultimg`, `content`, `adt`) VALUES
	(26, 1, 100, '想躲过拥挤人潮？上海迪士尼要这么玩', '', '', '', '<div class="f-block" style="margin: 0px; padding: 0px; word-wrap: break-word; color: rgb(102, 102, 102); font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti; font-size: 12px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;"><div class="p-section" style="margin: 13px 0px 28px; padding: 0px; word-wrap: break-word; font-size: 14px; line-height: 26px; word-break: break-all;">在迪士尼乐园，多玩项目肯定是一天的重点。上海迪士尼络绎不绝的人潮，如何才能规避冗长的队伍？有一项园区推出的措施可以让你合理“插队”！这项“插队”利器叫做<strong style="font-weight: bold; color: rgb(51, 51, 51);">快速通行卡(Fastpass)</strong>。</div></div><div class="f-block" id="_j_paragraph_1" style="margin: 0px; padding: 0px; word-wrap: break-word; color: rgb(102, 102, 102); font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti; font-size: 12px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;"><h2 style="margin: 18px 0px; padding: 0px; font-size: 24px; color: rgb(51, 51, 51);">巧用快速通行卡 Fastpass</h2></div><div class="f-block" id="_j_paragraph_2" style="margin: 0px; padding: 0px; word-wrap: break-word; color: rgb(102, 102, 102); font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti; font-size: 12px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;"><h3 style="margin: 15px 0px; padding: 0px; font-size: 18px; color: rgb(51, 51, 51);">什么是快速通行卡？</h3></div><div class="f-block" style="margin: 0px; padding: 0px; word-wrap: break-word; color: rgb(102, 102, 102); font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti; font-size: 12px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;"><img class="_j_lazyload" src="https://p4-q.mafengwo.net/s9/M00/DC/23/wKgBs1cUlruAGTLaAAHjoNEe4vc73.jpeg?imageView2%2F2%2Fw%2F680%2Fq%2F90" data-src="https://p4-q.mafengwo.net/s9/M00/DC/23/wKgBs1cUlruAGTLaAAHjoNEe4vc73.jpeg?imageView2%2F2%2Fw%2F680%2Fq%2F90" data-rt-src="https://p4-q.mafengwo.net/s9/M00/DC/23/wKgBs1cUlruAGTLaAAHjoNEe4vc73.jpeg?imageView2%2F2%2Fw%2F1360%2Fq%2F90" style="border: 0px; vertical-align: middle; max-width: 100%; margin: 10px 0px; display: block; height: 434px; width: 579px; background: rgb(252, 242, 220);" alt="" /><span class="img-an" style="color:#999999;">图：Fastpass专用通道与普通排队通道。</span></div><div class="f-block" style="margin: 0px; padding: 0px; word-wrap: break-word; color: rgb(102, 102, 102); font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti; font-size: 12px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;"><div class="p-section" style="margin: 13px 0px 28px; padding: 0px; word-wrap: break-word; font-size: 14px; line-height: 26px; word-break: break-all;">快速通行卡(Fastpass)是迪士尼乐园为应对游客较长等候时间而实行的一种游乐项目快捷搭乘方式。游客可以在热门项目上免费领取快速通行卡，在卡上显示的时间内返回该项目的排队区，即可不用排队直接体验此设施。</div></div><div class="f-block" id="_j_paragraph_3" style="margin: 0px; padding: 0px; word-wrap: break-word; color: rgb(102, 102, 102); font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti; font-size: 12px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;"><h3 style="margin: 15px 0px; padding: 0px; font-size: 18px; color: rgb(51, 51, 51);">哪里取快速通行卡？</h3></div><div class="f-block" style="margin: 0px; padding: 0px; word-wrap: break-word; color: rgb(102, 102, 102); font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti; font-size: 12px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;"><div class="p-section" style="margin: 13px 0px 28px; padding: 0px; word-wrap: break-word; font-size: 14px; line-height: 26px; word-break: break-all;">领取快速通行卡目前有两种方式，<strong style="font-weight: bold; color: rgb(51, 51, 51);">一种为利用门票，在乐园内4处快通卡服务中心提取，另一种为提前下载乐园官方APP，直接在APP上进行操作领取。</strong>直接使用APP也是最方便的方式，免除项目间来回奔走的劳顿。</div></div><div class="f-block" style="margin: 0px; padding: 0px; word-wrap: break-word; color: rgb(102, 102, 102); font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti; font-size: 12px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;"><div class="p-section" style="margin: 13px 0px 28px; padding: 0px; word-wrap: break-word; font-size: 14px; line-height: 26px; word-break: break-all;"><strong style="font-weight: bold; color: rgb(51, 51, 51);">服务中心现场领取</strong></div></div><div class="f-block" style="margin: 0px; padding: 0px; word-wrap: break-word; color: rgb(102, 102, 102); font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti; font-size: 12px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;"><div class="p-section" style="margin: 13px 0px 28px; padding: 0px; word-wrap: break-word; font-size: 14px; line-height: 26px; word-break: break-all;">乐园内共设有4处可以提取快速通行卡的服务中心。游客直接利用门票在取票机上扫码，并在屏幕上选择想要预约的项目，成功预约后便会拿到相应张数的快通卡。每个主题区的预约机只能预约该区域内的项目。如果有问题可以寻求在场的工作人员进行帮助。</div></div><div class="f-block" style="margin: 0px; padding: 0px; word-wrap: break-word; color: rgb(102, 102, 102); font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti; font-size: 12px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;"><div class="p-section" style="margin: 13px 0px 28px; padding: 0px; word-wrap: break-word; font-size: 14px; line-height: 26px; word-break: break-all;"><strong style="font-weight: bold; color: rgb(51, 51, 51);">服务中心位置：<br />明日世界</strong>“巴斯光年星际营救”前的广场，<strong style="font-weight: bold; color: rgb(51, 51, 51);">梦幻世界</strong>“冰雪奇缘”欢唱盛会“的斜对面及“七个小矮人矿山车”的对面，还有一处位于<strong style="font-weight: bold; color: rgb(51, 51, 51);">探险岛</strong>“雷鸣山漂流”的对面。</div></div><div class="f-block" style="margin: 0px; padding: 0px; word-wrap: break-word; color: rgb(102, 102, 102); font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti; font-size: 12px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;"><div class="p-section" style="margin: 13px 0px 28px; padding: 0px; word-wrap: break-word; font-size: 14px; line-height: 26px; word-break: break-all;"><strong style="font-weight: bold; color: rgb(51, 51, 51);">利用官方APP线上领取</strong></div></div><div class="f-block" style="margin: 0px; padding: 0px; word-wrap: break-word; color: rgb(102, 102, 102); font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti; font-size: 12px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;"><ul style="margin: 0px; padding: 0px; list-style: none;"><li style="margin: 0px; padding: 0px; list-style: none;">下载上海迪士尼度假区官方APP，并利用手机号或邮箱进行免费注册，创建账户。</li><li style="margin: 0px; padding: 0px; list-style: none;">进入乐园后，点击APP上“获取快速通行证”按钮，扫描手中乐园门票二维码进行绑定。同伴的门票可以统一绑定在一个账户下，方便同时多张领取快通卡。</li><li style="margin: 0px; padding: 0px; list-style: none;">绑定好即可根据提示领取热门项目的快通卡。</li><li style="margin: 0px; padding: 0px; list-style: none;">规定时间内返回该项目，扫描门票或手机APP上的二维码，即可不排队直接游玩项目。</li></ul></div><div class="f-block" id="_j_paragraph_4" style="margin: 0px; padding: 0px; word-wrap: break-word; color: rgb(102, 102, 102); font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti; font-size: 12px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;"><h3 style="margin: 15px 0px; padding: 0px; font-size: 18px; color: rgb(51, 51, 51);">如何使用快速通行卡？</h3></div><div class="f-block" style="margin: 0px; padding: 0px; word-wrap: break-word; color: rgb(102, 102, 102); font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti; font-size: 12px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;"><img class="_j_lazyload" src="https://b1-q.mafengwo.net/s9/M00/DA/6E/wKgBs1cUlU2APz_iAAF-dHpE9QQ98.jpeg?imageView2%2F2%2Fw%2F680%2Fq%2F90" data-src="https://b1-q.mafengwo.net/s9/M00/DA/6E/wKgBs1cUlU2APz_iAAF-dHpE9QQ98.jpeg?imageView2%2F2%2Fw%2F680%2Fq%2F90" data-rt-src="https://b1-q.mafengwo.net/s9/M00/DA/6E/wKgBs1cUlU2APz_iAAF-dHpE9QQ98.jpeg?imageView2%2F2%2Fw%2F1360%2Fq%2F90" style="border: 0px; vertical-align: middle; max-width: 100%; margin: 10px 0px; display: block; height: 450px; width: 600px; background: rgb(252, 242, 220);" alt="" /><span class="img-an" style="color:#999999;">图：东京迪士尼乐园Fastpass。</span></div><div class="f-block" style="margin: 0px; padding: 0px; word-wrap: break-word; color: rgb(102, 102, 102); font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti; font-size: 12px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;"><div class="p-section" style="margin: 13px 0px 28px; padding: 0px; word-wrap: break-word; font-size: 14px; line-height: 26px; word-break: break-all;">用门票取出快速通行卡后，卡上会写着相应的使用时间。例如游客早上10:00取出的Fastpass上面写着时间为12:30-13:30（如图），那么游客可以利用10:00-12:30的这段时间去玩其他游乐项目，无需在此排队。等到12:30-13:30的时候返回该项目，向工作人员出示卡片及门票，即可直接体验该项目而无需排队。</div></div><div class="f-block" id="_j_paragraph_5" style="margin: 0px; padding: 0px; word-wrap: break-word; color: rgb(102, 102, 102); font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti; font-size: 12px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;"><h3 style="margin: 15px 0px; padding: 0px; font-size: 18px; color: rgb(51, 51, 51);">可使用快速通行卡的项目</h3></div><div class="f-block" style="margin: 0px; padding: 0px; word-wrap: break-word; color: rgb(102, 102, 102); font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti; font-size: 12px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;"><div class="p-section" style="margin: 13px 0px 28px; padding: 0px; word-wrap: break-word; font-size: 14px; line-height: 26px; word-break: break-all;">七个小矮人矿山车<br />小飞侠天空奇遇<br />小熊维尼历险记<br />巴斯光年星际营救<br />创极速光轮<br />翱翔•飞越地平线<br />雷鸣山漂流</div></div><div class="f-block" id="_j_paragraph_6" style="margin: 0px; padding: 0px; word-wrap: break-word; color: rgb(102, 102, 102); font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti; font-size: 12px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;"><h2 style="margin: 18px 0px; padding: 0px; font-size: 24px; color: rgb(51, 51, 51);">单人通道 Single Rider</h2></div><div class="f-block" style="margin: 0px; padding: 0px; word-wrap: break-word; color: rgb(102, 102, 102); font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti; font-size: 12px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;"><div class="p-section" style="margin: 13px 0px 28px; padding: 0px; word-wrap: break-word; font-size: 14px; line-height: 26px; word-break: break-all;">迪士尼乐园排队区分三种通道，除普通排队通道（Stand&nbsp;by）和快速通行通道（Fastpass）外，还有一种叫做单人通道(Single&nbsp;Rider)。</div></div><div class="f-block" style="margin: 0px; padding: 0px; word-wrap: break-word; color: rgb(102, 102, 102); font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti; font-size: 12px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;"><div class="p-section" style="margin: 13px 0px 28px; padding: 0px; word-wrap: break-word; font-size: 14px; line-height: 26px; word-break: break-all;">单人通道是在设施装满普通通道和快速通道的游客之后，依然有空位的情况下，给单人游客准备的快速搭乘方式。因为一般单人通道都是分开的座位，甚至会不同车，如果小伙伴之间不介意分开体验，优先选择单人通道则会节省大量的时间。</div></div><div class="f-block" id="_j_paragraph_7" style="margin: 0px; padding: 0px; word-wrap: break-word; color: rgb(102, 102, 102); font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti; font-size: 12px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;"><h3 style="margin: 15px 0px; padding: 0px; font-size: 18px; color: rgb(51, 51, 51);">设有单人通道的设施</h3></div><div class="f-block" style="margin: 0px; padding: 0px; word-wrap: break-word; color: rgb(102, 102, 102); font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti; font-size: 12px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;"><div class="p-section" style="margin: 13px 0px 28px; padding: 0px; word-wrap: break-word; font-size: 14px; line-height: 26px; word-break: break-all;">加勒比海盗——沉默宝藏之战<br />七个小矮人矿山车<br />创极速光轮<br />雷鸣山漂流</div></div><div class="f-block" id="_j_paragraph_8" style="margin: 0px; padding: 0px; word-wrap: break-word; color: rgb(102, 102, 102); font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti; font-size: 12px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;"><h2 style="margin: 18px 0px; padding: 0px; font-size: 24px; color: rgb(51, 51, 51);">错峰游玩</h2></div><div class="f-block" id="_j_paragraph_9" style="margin: 0px; padding: 0px; word-wrap: break-word; color: rgb(102, 102, 102); font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti; font-size: 12px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;"><h3 style="margin: 15px 0px; padding: 0px; font-size: 18px; color: rgb(51, 51, 51);">早入园</h3></div><div class="f-block" style="margin: 0px; padding: 0px; word-wrap: break-word; color: rgb(102, 102, 102); font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti; font-size: 12px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;"><div class="p-section" style="margin: 13px 0px 28px; padding: 0px; word-wrap: break-word; font-size: 14px; line-height: 26px; word-break: break-all;">上午的游客相对较少，建议游客早些入园，用上午的时间多玩游乐设施，可以躲过冗长的队伍。</div></div><div class="f-block" id="_j_paragraph_10" style="margin: 0px; padding: 0px; word-wrap: break-word; color: rgb(102, 102, 102); font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti; font-size: 12px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;"><h3 style="margin: 15px 0px; padding: 0px; font-size: 18px; color: rgb(51, 51, 51);">晚离开</h3></div><div class="f-block" style="margin: 0px; padding: 0px; word-wrap: break-word; color: rgb(102, 102, 102); font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti; font-size: 12px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;"><div class="p-section" style="margin: 13px 0px 28px; padding: 0px; word-wrap: break-word; font-size: 14px; line-height: 26px; word-break: break-all;">乐园临近关门的两小时是乘坐游乐设施的最佳时间，这时段大部分游客已经离开乐园，游很多热门的游乐设施都可以不用排队直接搭乘。</div></div><div class="f-block" id="_j_paragraph_11" style="margin: 0px; padding: 0px; word-wrap: break-word; color: rgb(102, 102, 102); font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti; font-size: 12px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;"><h3 style="margin: 15px 0px; padding: 0px; font-size: 18px; color: rgb(51, 51, 51);">巡游时乘坐设备</h3></div><div class="f-block" style="margin: 0px; padding: 0px; word-wrap: break-word; color: rgb(102, 102, 102); font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti; font-size: 12px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;"><div class="p-section" style="margin: 13px 0px 28px; padding: 0px; word-wrap: break-word; font-size: 14px; line-height: 26px; word-break: break-all;">下午的巡游表演期间，大批的游客会聚集在巡演道路的两旁，如果你不介意错过花车巡演的话，巡演的这一小时时间，则可用来玩更多的游乐设施。</div></div><div class="f-block" id="_j_paragraph_12" style="margin: 0px; padding: 0px; word-wrap: break-word; color: rgb(102, 102, 102); font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti; font-size: 12px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;"><h2 style="margin: 18px 0px; padding: 0px; font-size: 24px; color: rgb(51, 51, 51);">巧用迪士尼官方APP</h2></div><div class="f-block" style="margin: 0px; padding: 0px; word-wrap: break-word; color: rgb(102, 102, 102); font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti; font-size: 12px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;"><img class="_j_lazyload" src="https://p4-q.mafengwo.net/s9/M00/91/B6/wKgBs1dzlaaAMzASAACG5HxoqPI27.jpeg?imageView2%2F2%2Fw%2F680%2Fq%2F90" data-src="https://p4-q.mafengwo.net/s9/M00/91/B6/wKgBs1dzlaaAMzASAACG5HxoqPI27.jpeg?imageView2%2F2%2Fw%2F680%2Fq%2F90" data-rt-src="https://p4-q.mafengwo.net/s9/M00/91/B6/wKgBs1dzlaaAMzASAACG5HxoqPI27.jpeg?imageView2%2F2%2Fw%2F1360%2Fq%2F90" style="border: 0px; vertical-align: middle; max-width: 100%; margin: 10px 0px; display: block; height: 254px; width: 321px; background: rgb(252, 242, 220);" alt="" /><span class="img-an" style="color:#999999;">图：上海迪士尼APP页面。</span></div><div class="f-block" style="margin: 0px; padding: 0px; word-wrap: break-word; color: rgb(102, 102, 102); font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti; font-size: 12px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;"><div class="p-section" style="margin: 13px 0px 28px; padding: 0px; word-wrap: break-word; font-size: 14px; line-height: 26px; word-break: break-all;">上海迪士尼乐园有官方的APP，不光可以购票，查看各日期的乐园营业时间。对于游客来说最有用的，就是查询当天乐园中各表演的时间及各项目的等候时间。游客仅需打开APP，就可在地图上看到各个项目的排队等候时间，而不用特意跑过去看，非常方便。</div></div>', '2018-07-19 16:52:48'),
